const getFullYear = () => {
  let curr_year = new Date().getFullYear();
  return curr_year;
};

const getFooterCopy = (isIdx) => {
  if (isIdx) {
    return "Holberton School";
  } else {
    return "Holberton School main dashboard";
  }
};

module.exports = { getFooterCopy, getFullYear, };
